const float scale = 1.5;
const vec2 center = vec2(0.0, 0.0);
const int iter = 20;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    xy -= iResolution.xy / 2.0;
    xy *= 2.0 / min(iResolution.x, iResolution.y);

    vec2 c = vec2(iMouse.xy / iResolution.xy) * 2.0 - 1.0;
    c.x -= 0.5;
    c.x *= 1.5;

    vec2 z;
    z.x = xy.x * scale - center.x;
    z.y = xy.y * scale - center.y;
    int j = 0;
    for(int i = 0; i < iter; i++) {
    	float x = z.x * z.x - z.y * z.y + c.x;
        float y = 2.0 * z.y * z.x + c.y;

        if((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;

        j++;
    }

	fragColor = vec4(vec3(float(j) / float(iter)),1.0);
}
