float signcos(in float v) {
    return cos(v)*.5+.5;
}
float height(in vec2 p) {
    vec2 uv = p;
	float res = 1.;
    for (int i = 0; i < 3; i++) {
        res += cos(uv.y*12.345 - iTime*4. + cos(res*12.234)*.2 + cos(uv.x*32.2345 + cos(uv.y*17.234)) ) + cos(uv.x*12.345);
    	uv = uv.yx;
        uv.x += res*.1;
    }
    return res;
}
vec2 normal(in vec2 p) {
    const vec2 NE = vec2(.1,0.);
    return normalize(vec2( height(p+NE)-height(p-NE),
                           height(p+NE.yx)-height(p-NE.yx) ));
}
vec3 diffuse(in vec2 p) {

    vec2 uv = p;
	float res = 1.;
    for (int i = 0; i < 3; i++) {
        res += cos(uv.y*12.345 - iTime*4. + cos(res*12.234)*.2 + cos(uv.x*32.2345 + cos(uv.y*17.234)) ) + cos(uv.x*12.345);
    	uv = uv.yx;
        uv.x += res*.1;
    }

    return texture(iChannel0, uv).xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;

    vec3 lightDir = normalize(vec3(sin(iTime),1.,cos(iTime)));

    vec3 norm3d = normalize(vec3(normal(uv),1.).xzy);
    vec3 dif = diffuse(uv);
    dif *= .25+max(0.,dot(norm3d,lightDir));

    vec3 view = normalize(vec3(uv,-1.).xzy);
    vec3 spec = texture(iChannel1, reflect(view,
                                               norm3d)).xyz*
        													max(0.,dot(-norm3d,view));
    fragColor = vec4(mix(dif,spec,.5),
                     1.);
}
