{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float y = -0.5;

    // On my 15" retina MacBook Pro, this produces a pure red screen
    // rather than a horizontal gradient from black to red.
	fragColor = vec4(atan(uv.x, -y), 0.0, 0.0,1.0);

    // But this equivalent line produces the gradient that every other
