void mainImage(out vec4 pixColor, in vec2 pixCoord) {
    vec2 pos = pixCoord.xy / iResolution.xy - vec2(.5 + sin(iTime) / 4., .5 + cos(iTime) / 4.);
    float curve = abs((sin(iTime * 1.5) / pos.x) * .00005 * pow(sin(iTime) * 5., 2.));
    pixColor = vec4(pos.y > -curve && pos.y < curve ? smoothstep(curve, curve * .3, pos.y) * smoothstep(curve, curve * .3, -pos.y) : 0.);
}
