void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float scale = 80.0;
    float width = 10.0;

	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = vec4(0,0,0,0);
    vec4 colorBg = vec4(uv.y,uv.x,uv.y,1);


    vec2 cord = vec2(fragCoord.x + 50.0 * sin(iTime), fragCoord.y);

    float dist = distance(cord,iResolution.xy/2.0);

    if(dist <= scale && dist >= scale - width)
    {
        color = vec4(0,0.7,1,1);
    }
    else
    {
    	color = colorBg;   
    }

	fragColor = color;
}
