//initialize colors
vec4 color1 = vec4(0.0,0.0,0.0,1.0);	//black
vec4 color2 = vec4(1.0,1.0,1.0,1.0);	//white

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set uv
	vec2 uv = fragCoord.xy / iResolution.xy;

    //set number of rows and columns
    float rows = 5.0;
    float columns = 8.0;           

    //find box size and spaces
    vec2 box = mod(vec2(uv.x * columns, uv.y * rows), 2.0);

    //color boxes
    if (box.x > 1.0 && box.y > 1.0 || box.x < 1.0 && box.y < 1.0)
        fragColor = color1;
    else
        fragColor = color2;
}
