vec3 hsv2rgb(vec3 color)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(color.xxx + K.xyz) * 6.0 - K.www);
    vec3 rgb = vec3(color.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), color.y));

    return rgb;
}

float mod289(float x) {
    return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec2 mod289(vec2 x) {
    return x - floor(x * (1.0 / 289.0)) * 289.0;
}

float euc(vec2 p0, vec2 p1) {
    vec2 d = abs(p0 - p1);
    return sqrt(d.x * d.x + d.y * d.y);
}

float man(vec2 p0, vec2 p1) {
	vec2 d = abs(p0 - p1);
    return d.x + d.y;
}

float che(vec2 p0, vec2 p1) {
	vec2 d = abs(p0 - p1);
    return max(d.x, d.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;
    uv += vec2(0.5);

    vec2 center = vec2(0.5);

    vec2 d = abs(uv - center);    
    d *= mod(iTime, 3.6);

    float offsetDistance = mod289(iTime / 4.4) / 1000.0;

    float euc = euc(center, uv) * mod(iTime, 3.6);
    float man = man(center + vec2(offsetDistance), uv) * mod(iTime, 2.6);
    float che = che(center - vec2(offsetDistance), uv) * mod(iTime, 5.6);

    float dist = mix(mix(euc,man, mod(mod289(iTime / 3.333), 1.0)), che, mix(euc, man, che));
    float dist1 = mix(dist, euc - che, che * che);

    fragColor = vec4(vec3(smoothstep(0.5, 0.54, dist), 1.0 - smoothstep(0.25, 0.29, dist), dist1), 1.0);
}
