void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    float dt = sin(mod(length((uv - vec2(0.8, 0.5)) * 5.) - iTime, 3.14));
	fragColor =  vec4(step(dt,0.5));
}

