vec2 map(vec3 p)
{
  vec3 q=p;
  float d = cappedcyl(p,vec2(2.,2.));
  q.y*=.75;
  q.x*=.9;

  q = (q-X*2.)*rot(pi_2,1.,.0,.0);
  d = min(d,torus(q,vec2(1.,.25))*.5);
  q=p;
  p=q-Y*.35;
  float inside = cappedcyl(p,vec2(1.85,1.9));
  d = max(d,-inside);
  vec2 res=vec2(d,.7);
  p.y-=-.3;
  inside = cappedcyl(p,vec2(1.85,1.9))-cos(atan(p.z,p.x)+iTime+length(p.xz)*12.)*.0125;
  res=opU(res,vec2(inside*.5,.805));
  res=opU(res,vec2(plane(p,up,2.),.9));
  res=opI(res,vec2(box(p-Y*1.5,vec3(3.75)),1.2));
  return res;
}

vec3 norm(vec3 p)
{
  return normalize(
    e.xyy*map(p+e.xyy).x+
    e.yxy*map(p+e.yxy).x+
    e.yyx*map(p+e.yyx).x+
    e.xxx*map(p+e.xxx).x);
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
  rd=normalize(rd);
  float res = 1.0;
  for( float t=mint; t < maxt; )
  {
    float h = map(ro + rd*t).x;
    if( h<0.0005 ) return 0.0;
    res = min( res, k*h/t );
    t += clamp(h,RMSH,.1);
  }
  return res;
}

