vec2 opU(vec2 a, vec2 b)
{
  return vec2(min(a.x,b.x),
    mix(a.y,b.y,1.-step(a.x,b.x)));
}

vec2 opS(vec2 a, vec2 b)
{
  return vec2(max(a.x,1.-b.x),
    mix(a.y,b.y,step(a.x,b.x)));
}

vec2 opI(vec2 a, vec2 b)
{
  return vec2(max(a.x,b.x),
    mix(a.y,b.y,step(a.x,b.x)));
}

float pow50(float x)
{
	float x5 = x*x*x*x*x;
    float x25=x5*x5*x5*x5*x5;
