}

vec2 rep(vec2 p,float n)
{
  float a=atan(p.y,p.x);
  a=mod(a,pi2/n)-pi/n;
  return length(p)*vec2(cos(a),sin(a));
}

vec3 rgb2hsv(vec3 c)
{
  vec4 k = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
  vec4 p = mix(vec4(c.bg, k.wz), vec4(c.gb, k.xy), step(c.b, c.g));
  vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));
  float d = q.x - min(q.w, q.y);
  float e = 1.0e-10;
  return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
  vec4 k = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + k.xyz) * 6.0 - k.www);
  return c.z * mix(k.xxx, clamp(p - k.xxx, 0.0, 1.0), c.y);
}

mat2 rot2(float a)
{
  float c=cos(a),s=sin(a);
  return mat2(c,-s,s,c);
}

mat3 rot(float a,float x,float y,float z)
{
  float s=sin(a),c=cos(a);
  return mat3(
    x+c*y+c*z,s*z,s*y,
    -s*z,y+c*x+c*z,s*x,
    -s*y,-s*x,z+c*x+c*y);
}

mat3 rotX(float a)
{
  float s=sin(a),c=cos(a);
  return mat3(
    1.,0.,0.,
    0.,c,s,
    0.,-s,c);
}

mat3 rotY(float a)
{
  float s=sin(a),c=cos(a);
  return mat3(
    c,0.,s,
    0.,1.,0.,
    -s,0.,c);
}

mat3 rotZ(float a)
{
  float s=sin(a),c=cos(a);
  return mat3(
    c,s,0.,
    -s,c,0.,
    0.,0.,1.);
}

float lengthN(vec3 p,float n)
{
  p=pow(p,vec3(n));
  return pow(p.x+p.y+p.z,1./n);
}

float lengthN(vec2 p,float n)
{
  p=pow(p,vec2(n));
  return pow(p.x+p.y,1./n);
}

float plane(vec3 p,vec3 n,float d)
{
  return dot(p,n)+d;
}

float box( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,.0));
}

float torus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float torus88(vec3 p,vec2 t)
{
  vec2 q=vec2(lengthN(p.xz,8.)-t.x,p.y);
  return lengthN(q,8.)-t.y;
}

float torus28(vec3 p,vec2 t)
{
  vec2 q=vec2(lengthN(p.xz,2.)-t.x,p.y);
  return lengthN(q,8.)-t.y;
}

float sphere(vec3 p,float r)
{
	return length(p)-r;
}

float cone( vec3 p, vec2 c )
{
    return dot(normalize(c),vec2(length(p.xy),p.z));
}

float cappedcyl( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

mat3 camera(vec3 ro, vec3 ta, float cr)
{
  vec3 cw = normalize(ta-ro);
  vec3 cp = vec3(sin(cr),cos(cr),.0);
  vec3 cu = normalize(cross(cw,cp));
  vec3 cv = normalize(cross(cu,cw));
  return mat3(cu,cv,cw);
}

vec2 pol(float a, float r)
{
	return vec2(cos(a),sin(a))*r;
}
