void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // GLSL/HLSL、CG言語等のシェーダプログラミング用言語で、
    // 最も特徴的な演算子がswizzle演算子です.

    // swizzle演算子はベクトル型への要素アクセス方法で、
    // 下記の例であれば(x:1.0, y:0.0, z:0.0, w:1.0)の赤色に対して、
    // (y:0.0, y:0.0, x:1.0, w:1.0)とアクセスすることで青色に変えています.
    vec4 color = vec4(1.0, 0.0, 0.0, 1.0);
	fragColor = color.yyxw;
}
