// quick 2D wave experiment
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = vec4(0.2823529411764706, 0.39215686274509803, 0.9450980392156862, 1.);
	vec2 uv = fragCoord.xy / iResolution.xy;
    int WAVES = 5;
    for (int i=1; i < WAVES; i++){
        if (
            uv.y < (
                sin(uv.x * 6. + iTime * pow(2., float(i + 1)))
                / pow(4., float(i +1)) +
                sin(uv.x * 10. + iTime * pow(1.5, float(i))) / 10.
                + 1. - pow(.75, float(i))
               ) + sin(iTime/10.)
        ){
            fragColor = color / pow(2., float(i));
            break;
        }
    }
}
