/*
	----------------
	Hypnotic checker
	----------------

	UV offsets, mirroring and distortion on a checker

	Francois 'CoyHot' Grassard - 2017
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((fragCoord.xy / iResolution.xy)-0.5)/vec2(iResolution.y / iResolution.x, 1);

	uv.x = uv.x*exp(uv.y*4.);

	if (uv.x>0.){uv.x = -uv.x;}
	if (uv.y>0.){uv.y = -uv.y;}

	uv.y += uv.x/5.;


  fragColor = vec4(sin((atan(exp(abs(uv.x)*(-1.)),abs(uv.y))+iTime/10.+uv.x)*50.5)*(sin(uv.y*50.)*10.));    

}
