/**/ // original test case :
void mainImage( out vec4 O, vec2 U )
{
	vec2 R = iResolution.xy;
    U = ( U+U-R)/R.y;
    float l = length(U),d;
    for( float i=0.; i<10.; i++) {
        d = dFdx(l);
     // d = length(vec2(dFdx(l),dFdy(l)));  // constant 1 
        if ( i/10. > l ) break;
    }
    if (U.x<0.) d = dFdx(l); // for comparison, on left side
	O = vec4(abs(d)*R.y/2.);
}
