#define seconds iDate.w
#define yellow vec4(1, 1, 0, 1)

void mainImage( out vec4 fragColor, vec2 coords )
{
    coords = ( coords+coords - iResolution.xy ) / iResolution.y; // Half height, centered
	float radius = length(coords);
    fragColor = yellow * 
        step(radius, .5) * 
        step( coords.x / radius , .9-.1 * sin(seconds));
}
