struct circle
{
    vec2 pt;
    float r;
    float d;
    float k;
};

circle c[5];


void move(circle c[5])
{
}

vec4 colorize(vec2 pos, circle c[5])
{
    vec4 color;

    for(int i=0;i<5;i++)
    {
        c[i].d = distance(c[0].pt,pos);
        c[i].k = c[i].d / c[i].d;
        c[i].k = clamp(c[i].k,0.0,1.0);
    }

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    c[0].pt = iMouse.xy / iResolution.xx;


	vec2 uv = fragCoord.xy / iResolution.xx;

    fragColor = colorize( uv, c );
}

vec2 mainSound( in int samp, float time )
{
    return vec2( sin(6.2831*440.0*time)*exp(-3.0*time) );

}
