#define rotationmat2(spin) mat2(sin(spin),cos(spin),cos(spin),-sin(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec4 colorsecond = vec4(0.8, 0.2, 0.5, 1.0);
    vec4 colorminute = vec4(0.2, 0.5, 0.8, 1.0);
    vec4 colorhour   = vec4(0.5, 0.8, 0.2, 1.0);
    vec2 normal, coord;
    float spin;

	vec2 uv = fragCoord.xy / iResolution.y * 2.0 - iResolution.xy / iResolution.y;

    fragColor = vec4(0.0);


    //seconds
    spin = floor(mod(iDate.w, 60.0)) / 60.0 * 6.28;
    //animation
    spin += min(fract(mod(iDate.w, 60.0)) / 60.0 * 6.28 * 10.0, 6.28 / 60.0);

    coord = uv * rotationmat2(spin);

    fragColor += (sign(0.03 - length(vec2(-max(abs(coord.x - 0.45) - 0.45, 0.0), coord.y))) / 2.0 + 0.5) * colorsecond;


    //minutes
    spin = floor(     mod(iDate.w / 60.0, 60.0)) / 60.0 * 6.28;
    //animation
    spin += min(fract(mod(iDate.w / 60.0, 60.0)) * 6.28 * 10.0, 6.28 / 60.0);

    coord = uv * rotationmat2(spin);

    fragColor += (sign(0.07 - length(vec2(-max(abs(coord.x - 0.45) - 0.45, 0.0), coord.y))) / 2.0 + 0.5) * colorminute;


    //hours
    spin = floor(mod(iDate.w / 3600.0, 12.0)) / 12.0 * 6.28;
    //animation
    spin += min(fract(mod(iDate.w, 60.0)) * 6.28 * 10.0, 6.28 / 60.0);

    coord = uv * rotationmat2(spin);

    fragColor += (sign(0.05 - length(vec2(-max(abs(coord.x - 0.45) - 0.45, 0.0), coord.y))) / 2.0 + 0.5) * colorhour;

    //fancy circles
    fragColor += sign(abs(length(uv) - 0.55) - 0.45) / 2.0 + 0.5;
}
