float circulo(in vec2 p, float rad, vec2 pos){

    p -=pos;
    p = p-vec2(0.0,-0.4);
	p.y -= 0.6*abs(cos(cos(p.x*1.4) + 4.2*iTime));
    return 1.-step(rad, length(p));
}

float circulo2(in vec2 p, float rad, vec2 pos){

    p -=pos;
    p = p-vec2(0.0,-0.4);
	p.y -= 0.6*abs(sin(cos(p.x*1.4) + 4.2*iTime));
    return 1.-step(rad, length(p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x/iResolution.y;


    float cir = circulo(p,0.5,vec2(-1.0,0.0));
    float cir2 = circulo2(p,0.5,vec2(1.0,0.0));

    vec3 col = vec3(0.0);

    col += vec3(cir)*vec3(1.0,1.0,0.0);
    col += vec3(cir2)*vec3(1.0,0.0,0.0);

	fragColor = vec4(col,1.0);
}
