void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;



    vec2 tc = uv;
    //uvA -= vec2(0.5, 0.5);
    vec2 mouse = uv - (iMouse.xy / iResolution.xy);


    float dist =  sqrt(dot(mouse, mouse));

    float time = iTime / 5.0;

    float circle_radius = mod( time, 1.0);
    float border = 0.05;

    float t = 1.0 + smoothstep(circle_radius, circle_radius+border, dist)
       			 - smoothstep(circle_radius-border, circle_radius, dist);

    if( t < 1.0 )
    {
        vec2 p = -1.0 + 2.0 * tc;
        float len = length(p);
        vec2 uv = tc + (p/len)*cos(len*6.0-time*4.0)*0.3;
        vec3 col = texture(iChannel0,uv).xyz;
        gl_FragColor = vec4(col,1.0);
    }
    else
    {
        vec3 col = texture(iChannel0,uv).xyz;
        gl_FragColor = vec4(col,1.0);
    }

	fragColor = gl_FragColor;
}
