renderpass.code:
vec3 fromIRGB(vec3 c) {
    return c/255.;
}
float hash( float n ) { return fract(sin(n)*753.5453123); }
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0 + 113.0*p.z;
    return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+157.0), hash(n+158.0),f.x),f.y),
               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                   mix( hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}
float h(vec2 uv) {
    return noise(vec3(uv.xy*5.+iTime, iTime*2.))+texture(iChannel1, uv/5.).r+texture(iChannel0, uv).r;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c = vec4(0);
	vec2 uv = fragCoord.xy / iResolution.xy;
    float y = .5*sin(clamp(fract(iTime/(3.14*1.5-1.))/(3.14*1.5-1.), 3.14/2.+.1, 3.14*1.5-.1))+.5;
    if (abs(y-h(uv))<.2) {
    	  c= vec4(fromIRGB(vec3(47, 112, 216)), 1)*smoothstep(-1.,1.,abs(y-h(uv))*10.)*2.;  
    }
	fragColor = c;
}
