void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 col = texture(iChannel0, uv)+texture(iChannel0, vec2(1.-uv.x,uv.y));
	//col *= 0.33;
    col = clamp(col,0.,1.);
    fragColor = col;
}

#define ITER 256
#define ITER2 20

float seed = 0.;
float iter = float(ITER);
float bailout = 8.;


float rand() { return fract(sin(seed++)*62758.5453123); }

vec2 csqr( vec2 a ) { return vec2(a.x*a.x-a.y*a.y, 2.0*a.x*a.y ); }


bool skipCardioid(vec2 p){

	vec2 n = p*p;
	float q = n.x - p.x/2.0 + 0.125 + n.y;
	//skip main cardioid
	if (q * (q + (p.x - 0.25)) < n.y*0.25)return true;
	//skip left sphere 
	if (n.x + 2.0*p.x + n.y < -0.9375)return true;
	return false;
}


vec3 mandel(vec2 p,vec2 c){

	if(skipCardioid(c))return vec3(0.);
	vec2 z= c;	
	float r2=0.;
	float ot=10.;
    float sqrRes = iResolution.x*iResolution.x;
	for(int i = 0; i<ITER;i++){
		z=csqr(z)+c;		
		float ot1=abs(dot(z-p,p-c));
        r2=dot(z-p,z-p);
		if(r2>bailout){
			float it=float(i)/iter;
			return it*vec3((1.-it)*.5,0.5*it,it)*min(sqrRes,exp((9.-ot*sqrRes*.05) ))*1.;
		}
		ot=min(ot,ot1);	
        ot=min(ot,r2);
	}
	return vec3(0.);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 previous = texture(iChannel0, (fragCoord)/iResolution.xy);
    seed = iTime;
    vec3 color = previous.rgb;
    float zoom = 1.5;
   // if(fract(iTime)<.2)zoom = 1.5;
   	vec2 p = zoom*(iResolution.yx-2.0*fragCoord.yx)/iResolution.y;
    for(int i = 0; i<ITER2;i++){
		vec2 c= 2.*vec2(rand(),1.-rand())-1.;
		c.x=c.x*1.5-.5;
		float k = exp(-iTime*0.005-9.);
        color = (1.-k)*color+k*mandel(p,c).bgr;
    }
    fragColor = vec4(color,1.0);
}
