void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    
    vec4 col = texture(iChannel0, uv)+texture(iChannel0, vec2(1.-uv.x,uv.y));
    col = clamp(col*16.,0.,1.);
    fragColor = col;
}

#define ITER 512
#define ITER2 40

float seed = 0.;
float iter = float(ITER);
float res = 1.;


float rand() { return fract(sin(seed++)*62758.5453123); }

vec2 csqr( vec2 a ) { return vec2(a.x*a.x-a.y*a.y, 2.0*a.x*a.y ); }


bool skipCardioid(vec2 p){

	vec2 n = p*p;
	float q = n.x - p.x/2.0 + 0.125 + n.y;
	//skip main cardioid
	if (q * (q + (p.x - 0.25)) < n.y*0.25)return true;
	//skip left sphere 
	if (n.x + 2.0*p.x + n.y < -0.9375)return true;
	return false;
}


vec3 mandel(vec2 p,vec2 c){

	if(skipCardioid(c))return vec3(0.);
    if(abs(p.y)>1.5)return vec3(0.);
	vec2 z= c;	
	float r2=0.;
	float ot=10.;
    float sqrRes = iResolution.x*iResolution.x;
	for(int i = 0; i<ITER;i++){
		z=csqr(z)+c;		
		float ot1=dot(z-p,z-p);
        r2=dot(z,z);
		if(r2>4.){
			float it=float(i)/iter;
            return it*vec3(.8*it,.5*it,(1.-it)*.6)*min(1.,exp((9.-ot*sqrRes)/(res*res)))*5./res*iResolution.x;
		}
		ot=min(ot,ot1);		
	}
	return vec3(0.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 previous = texture(iChannel0, (fragCoord)/iResolution.xy);
    seed = iTime;
    vec3 color = previous.rgb;
    res = max(exp(-iTime*0.002)*20.,1.);
	vec2 p = 1.5*(iResolution.yx-2.0*fragCoord.yx)/iResolution.y;
    for(int i = 0; i<ITER2;i++){
		vec2 c= 2.*vec2(rand(),1.-rand())-1.;
		c.x=c.x*1.5-.5;
		float k = 1e-4/(1.+iTime*.01);
        color = (1.-k)*color+k*mandel(p,c);
    }
    fragColor = vec4(color,1.0);
}
