vec2 tunnel(vec2 uv) {
    return vec2(
        max(0.0, 1.0 / length(uv)),
        3.1415 + atan(uv.x, uv.y)
        );
}

vec3 map(vec2 uv, float t) {
    vec2 d = texture(iChannel0, uv + vec2( 0.0, t)).xy;
    uv.y += 0.25 * d.y;
	return vec3(
        sin(3.1415 * fract(2.0 * uv.x)),
    	abs(cos(2.0 * (uv.y + uv.x))),
    	abs(sin(2.0 * (uv.y + uv.x)))
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 2.0 * iTime;
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;    
    uv.x *= iResolution.x / iResolution.y;
    uv += .1 * vec2(sin(1.37 * t), cos(1.11 * t));
    vec2 b = tunnel(uv);
    vec3 color = map(b + vec2(t + .25 * sin(t), sin(b.x + t)), t) / b.x;
	fragColor = vec4(color,1.0);
}
