bool CheckCircle(vec2 centerPoint, float radius, vec2 currPoint)
{
    if(length(currPoint - centerPoint) <= radius)
    {
        return true;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //PREMADE VARIABLES
    float recWidth  = .1;
    float recHeight = .3;
    vec2  recCenter = vec2(.8,.5);

    vec2 circleCenter1 = vec2(.2,.2);
    vec2 circleCenter2 = vec2(.3,.2);
    vec2 circleCenter3 = vec2(.25,.3);

    vec2 circleUV;

    float circleColor1 = .7;
    float circleColor2 = .5;
    float circleColor3 = .3;
    float rectangleColor = .5;

    vec4 BG;
    vec4 Layer1;
    vec4 Layer2;
    vec4 Layer3;
    vec4 Layer4;

    float circleSharedRadius = .1;

	vec2 uv = fragCoord.xy / iResolution.xy;
    circleUV = uv;

    circleUV.y *= (iResolution.y / iResolution.x);    

    BG = vec4(uv,0.5+0.5*sin(iTime),1.0);

    //ENDPREMADE VARIABLES

    //MOVE RECTANGLE
    recCenter.x *= sin(iTime) * .5;
    recCenter.x += .5;

    //check rectangle
    if(length(uv.x - recCenter.x) <= recWidth &&
       length(uv.y - recCenter.y) <= recHeight)
    {

        Layer1 = BG * rectangleColor;
    }

    //checkCircle1    
    if(CheckCircle(circleCenter1, circleSharedRadius, circleUV))
    {
        Layer2 = BG * circleColor1;
    }
    //checkCircle2  
    if(CheckCircle(circleCenter2, circleSharedRadius, circleUV))
    {
        Layer3 = BG * circleColor2;
    }
    //checkCircle3   
    if(CheckCircle(circleCenter3, circleSharedRadius, circleUV))
    {
        Layer4 = BG * circleColor3;
    }

    vec4 Composite = mix(BG, Layer1, Layer1.a);
    Composite = mix(Composite, Layer2, Layer2.a);
    Composite = mix(Composite, Layer3, Layer3.a);
    Composite = mix(Composite, Layer4, Layer4.a);

    fragColor = Composite;    
}
