float rand(vec2 p){
  return fract(sin(dot(p ,vec2(12.9898,78.233))) * 43758.5453);
}



//f : freq
//v: velocity
//a: amp
float wave(float x , float f,float v , float a){
    return  sin(x*f + iTime*v)*a ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float asp = iResolution.y/iResolution.x;
    uv.y *= asp;

    float grad = 3.0;
    vec3 col = vec3(.3,.6,.8);



    float f = 0.0;
    float po = sin(iTime);
    float yo = tan(iTime*1.0);

    f+=wave(uv.x , 8.0 , 2.0 , 0.06);
    f+=wave(uv.x , 4.0 , 4.0 , 0.02);
    f+=wave(uv.x , 10.0+po*3.0, -3.4 , sin(iTime*5.0)*0.01);
    f+=wave(uv.x , 60.0 * sin(iTime*0.1) , -2.0,min(1.0,yo*yo)*0.02);
    f+=wave(uv.x -0.5 , 30.0 * min(3.0,yo*yo) , 4.0 , 0.02);


    f += 0.20;



    if(uv.y -f<0.01){
        col = vec3(.8,.6,.4);
        if(uv.y-f > 0.006){
            col = vec3(0.8,0.9,1);
            //col.y -= rand(uv + vec2(f))*0.2;
        }		
    }

    // Time varying pixel color

   col.z -= uv.x*uv.x/grad;
    col.z -= (1.0-uv.x)*(1.0-uv.x)/grad;
    col.y -= uv.x*uv.x/grad;
    col.y -= (1.0-uv.x)*(1.0-uv.x)/grad;



    // Output to screen
    fragColor = vec4(col,1.0);
}
