void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = vec2(fragCoord.xy / iResolution.xy);
	vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
	float t = iTime;
    r = r * 5.;
    float x = -2.6 + cos(iTime) * 5.2;
    float y = 0.0;
    float dx = 4.0 + cos(iTime) * .01;
    float dy = 4.0 + sin(iTime) * .01;

    float angle = iTime*1.;


    float v1 = sin(r.x +t*.5);
    float v2 = sin(r.y +t*-.5);
    float v3 = sin(r.x+r.y +t);
    float v4 = sin(length(r) +.7*t);

    float v5 = cos(r.x +t*-.5);
    float v6 = cos(r.y +t*.5);
    float v7 = cos(r.x+r.y +t);
    float v8 = cos(length(r) -.7*t);

    float v9  = tan(r.x +t);
    float v10 = tan(r.y +t);
    float v11 = tan(r.x+r.y +t);
    float v12 = tan(length(r) +1.7*t);


	float v = v1+v2+v3+v4;

    v = v1+v2+v3+v4;
    v2 = v5+v6+v7+v8;
    v3 = v9+v10+v11+v12;

    v3=smoothstep(v3,v2,0.5);

	vec3 ret;

	ret = vec3(sin(v), sin(v+v2), sin(v+v3));

	ret = fract(ret) * 1.3;

    vec3 pixel = ret;
    fragColor = vec4(pixel, 1.);
}
