#define S(a,b,t) smoothstep(a,b,t)
#define DST 0.001
#define WD 1.5
#define STP 5.
#define RES 400.
#define RES2 600.
#define RES3 900.
#define nTime iTime/2.

float Dline(vec2 p, vec2 a, vec2 b) {
	// line drawing function from BigWings
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.1);
    return length(pa-ba*t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float s = sin(nTime/5.);
    float c = cos(nTime/5.);
    uv*=mat2(c, -s, s, c); 

    float va = (cos(iTime/6.)+.5)*20.;
    float shape = 3.+(cos(iTime/35.)*2.);

    float col=0.0;
    float i=-100.0;
    for(int i2=0;i2<1000;i2++)
    {
        i+=shape;
        if (i >= 100.) {break;}
        float r=va;
        float rr=120.;
        float p=va;
        float ii=i+shape;
    	vec2 myA = vec2((r+rr)*cos(i)+p*cos(r+rr)*i/r,(r+rr)*sin(i)+p*sin(r+rr)*i/r);
    	vec2 myB = vec2((r+rr)*cos(ii)+p*cos(r+rr)*ii/r,(r+rr)*sin(ii)+p*sin(r+rr)*ii/r);

    	float d = Dline(uv, myA/RES, myB/RES);
   	 	float m = S(WD/200., (WD/200.)-(0.02-(i/9000.)), d)*WD;
    	col = max(m,col);	

        d = Dline(uv-vec2(0.0,0.0), myA/RES3, myB/RES3);
   	 	m = S(WD/200., (WD/200.)-(0.02-(i/9000.)), d)*WD;
    	col = max(m,col);

    }
    fragColor = vec4(vec3(col*(cos(iTime/15.)/2.+1.2),col*(sin(iTime/23.)/2.+1.2), col*(sin(iTime/10.)/2.+1.2)),1.0);
}
