//Ethan Alexander Shulman 2018 - https://xaloez.com/

float hmap(vec2 p) {
    return sin(p.x*.2)*1.6+cos(p.y*.5)+sin(p.y*.08)*2.3;
}

void mainImage( out vec4 o, in vec2 u)
{
    vec3 rd = normalize(vec3((u*2.-iResolution.xy)/iResolution.x,1.)),
        rp = vec3(0,6.,0.);

    float ang = iTime*.1;
    rd.xz *= mat2(cos(ang),-sin(ang),sin(ang),cos(ang));

    if (rd.y > 0.) {
    	o = vec4(0.);
        return;
    }

    float s = 4.;
    int i;
    for (i = 0; i < 64; i++) {
        if (rp.y < hmap(rp.xz)) {
            if (s > .01) {
                rp -= rd*s;
                s *= .5;
            } else break;
        }
        rp += rd*s;
    }
    if (i < 64) {
        vec3 p1 = vec3(rp.x,0.,rp.z), p2 = vec3(rp.x+.1,0.,rp.z), p3 = vec3(rp.x,0.,rp.z+.1);
        p1.y = hmap(p1.xz);
        p2.y = hmap(p2.xz);
        p3.y = hmap(p3.xz);
        vec3 nrm = cross(normalize(p2-p1),normalize(p3-p1));
        o = vec4(abs(nrm)*(.5+sin(iTime*vec3(.2,.14,.27))*.5),1);
    } else o = vec4(0.);
}
