float hash( float n )
{
	return fract(cos(n)*43758.5453);
}

float noise( in vec2 x )
{
	vec2 p = floor(x);
	vec2 f = fract(x);
    	f = f*f*(3.0-2.0*f);
    	float n = p.x + p.y*57.0;
    	float res = mix(mix( hash(n+  0.0), hash(n+  1.0),f.x), mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
    	return res;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pt = 4.5*(fragCoord.xy / iResolution.xy - 0.5)*vec2(iResolution.x/iResolution.y,2);
    float rInv = 0.5/length(pt);

	float f = noise( 2.0*pt );

	float cover = 0.085;
	float sharpness = 0.0015;

	float c = f - (1.0 - cover);
	if ( c < 0.0 )
		c = 0.0;

	f *= 1.0 - (pow(sharpness, c));

    pt =  rInv + vec2(rInv + (iTime/20.),4.5);
    fragColor = mix(texture(iChannel0,pt),texture(iChannel1,pt),0.8)*rInv;
}
