// exaggerate 
#define silly true

// animation rate
#define fast  true

// *only* works in conjuntion with 'silly' macro
// distance from surface (fake 3d)
#define distance 10.0

// math stuff
#define PI 		3.14159265359
#define TWOPI  	PI*2.0

// suspiciously works well with 128bpm 
#define FOURPI 	TWOPI*2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // colours
    vec3 col0 = vec3(0.2,0.2,0.4);
    vec3 col1 = vec3(0.0,1.0,0.5);

    // effect
    float dist	  = length(fragCoord.xy - iMouse.xy) * (mix(1.0, distance * 0.01, silly));
    float falloff = max(sin(iTime* mix(.5, FOURPI, fast)) + 5.0, 2.0) / dist;
    float ambient = mix(0.0,(clamp(sin(iTime * 0.5 * mix(.5, 5.0, fast)), 0.04, 0.05)) + 0.1, silly);

    // Output to screen
    vec3 col  = mix(col0.rgb + col1.rgb, col0.rgb, falloff ) * (falloff + ambient);
    fragColor = vec4(col,1.0);
}
