// Entry for [2TC 15] (= 280 chars or less, as counted by Shadertoy)

void mainImage (out vec4 f, vec2 w) {
	vec3 r = vec3 (w / iResolution.y - .5, 2), p = vec3 (-20, 30, 1), q;
	float d = .6, c = 0., l = c;
	r.y -= d;
	for (int i = 0; i < 99; ++i)
		if (d > .1 && l < 99.)
			q = p + sin (p.z * .2 + iDate.w),
			l += d = (length (q.xy) - 4. + sin (abs (q.x * q.y) + p.z * 4.) * sin (p.z)) * .1,
			p += r * d,
			c += .01;
	f = c * vec4 (2, 1, 0, 1);
}
