void mainImage( out vec4 f, in vec2 w )
{
	float b = sin(iDate.w), d;
    vec3 y = vec3(0,0,.5), n = y, p, g = iResolution;
    n.xy = abs(w / g.x - y.zy);
    p = n;
    for(int k=0; k<64; ++k)
    {
        d = 2.;
        for(float t=2.; t<9.; t+=.5) {
            g = p - vec3(d/t,t-b,16);
        	d = min(d, length(g) - 1.);
        }
        p += n*d;
    }
    f = dot(g,-y)*vec4(2,1,1,1)*(1.-d);
}
