float fractal(vec2 coord)
{
    vec2 z = 4.0 * ( coord / iResolution.xx - vec2(0.5, 0.5 * iResolution.y / iResolution.x));
	vec2 c = vec2(0.71 + cos(765.0 * iTime / 943.0) / 64.0, 0.125 + sin(654.0 * iTime / 791.0) / 64.0);
    float power = 6.0;

    for(int n = 0; n < 32; ++n)
    {
        float r = sqrt(z.x * z.x + z.y * z.y);
        float phi = power * atan(z.y / z.x);
        z = pow(r, power) * vec2(cos(phi), sin(phi)) + c;
    }

    return length(z) < 4.0 ? 1.0 : 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = 0.0;

    for(float y = 0.0; y < 3.0; ++y)
    {
        for(float x = 0.0; x < 3.0; ++x)
        {
            c += fractal(fragCoord.xy + vec2(x / 3.0, y / 3.0));
        }
    }

    c /= 9.0;

	fragColor = vec4(c, c, c, 1.0);
}
