precision highp float;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1. + 2. * fragCoord.xy / iResolution.xy;
    vec2 offset = vec2(.3, .3);

    float radius = sin(iTime + sqrt(dot(p, p)));
    float radius2 = sin(iTime + sqrt(dot(p - offset, p - offset)));
    float radius3 = sin(iTime + sqrt(dot(p + offset, p + offset)));

    bool toggle1 = mod(radius, .1) > .01;
    bool toggle2 = mod(radius2, .1) > .01;
    bool toggle3 = mod(radius3, .1) > .01;

    //float col = toggle || toggle2 || toggle3 ? 1. : 0.0;
    float col = 0.0;
    if (toggle1) col = 1.0; 
    if (toggle2) col = 1.0;
    if (toggle3) col = 1.0;
    if ((toggle1) && (toggle2) && (toggle3)) col = 0.0;

//    fragColor = vec4(toggle, toggle2, toggle3, 1.); 
    fragColor = vec4(col,col,col,1.0);
}
