// Title: The Bridge between Dimentions  , Author: Marcin Miko≈Çajczyk 
// This shader is prepared for the first Wroclaw Shader Competition
// organized by Faculty of Physics and Astronomy, Khronos Chapter and SIGGRAPH Chapter from Wroclaw

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = (-3.5+7.0*uv)*vec2(iResolution.x/iResolution.y,1.0);


    int soundTx = int(uv.x*512.0);

    float wave = texelFetch( iChannel0, ivec2(soundTx,1), 0 ).x;

    float d = length(p/1.0  - vec2(sin(iTime*0.1),cos(iTime*0.4)));
    p = p/1.0 - vec2(sin(iTime*0.1),cos(iTime*0.4));

    d = smoothstep(0.4,0.5,d * d * (-2.0*sin((wave/1.5) - uv.y)) + 2.0/sin(wave + uv.y))*d*d;
    float phi;
    phi = atan(p.y/p.x)-iTime*0.8;

    d *= sin(phi*46.0);


	vec3 col = vec3(atan(d),0.1,-sqrt(d/p*p)) + d;

    col *= sin(iTime/d*d);
    col -= d/1.2;
    col *= 1.0*sin((-(p.x*p.x+p.y*p.y)*2.0-iTime*6.0)/-(p.x*p.x+p.y*p.y)/tan((iTime))*0.5);

    fragColor = vec4(col,1.0);
}
