void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 z, c;

	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.x -= 0.5f;
    uv.y -= 0.5f;
    uv.x *= 3.0f;
    uv.y *= 3.0f;

    z.x = uv.x;
    z.y = uv.y;

    c.x = (iMouse.x / iResolution.x-0.5f) + cos(iTime / 5.0f) / 25.0f;
    c.y = (iMouse.y / iResolution.y-0.5f) + sin(iTime / 5.0f) / 25.0f;

    int i;

    for(i=0; i < 100; i++) {
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (z.y * z.x + z.x * z.y) + c.y;

        if((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;
    }

    fragColor = vec4(1,1,1,1) * float(i) / 100.0;
}
