void mainImage( out vec4 o, vec2 i ) {
	float s	=	iResolution.y / 4.,
	t		=	mod( floor( i.y / s ), 2. );
	i		=	mod( i, s );
	i.y		+=	t * ( s - 2. * i.y );
	o		=	abs( sin( vec4( step( i.x, s - i.y / 2. ), step( i.x, i.y / 2. ) - t, i ) + iDate.w / 8. ) );
}
