#define SMALL_AMOUNT 0.0001
#define SAMPLES 30.0

// POST PROCESSING:
//
// In case you're interested in only watching the Ray Marching algorithm in action,
// keep uncommented the following line:
#define RAY_MODE

// If you wish to see all the post processing effects in a quick demo, comment the previous line
// and uncomment the next line:
#define DEMO_MODE


#ifdef RAY_MODE
	#undef  DEMO_MODE
	#define POST_PROCESS 0
#endif

#ifndef RAY_MODE && DEMO_MODE
// If you liked a specific post-fx, please specify the number below.
// Also, make sure both the DEMO_MODE and RAY_MODE lines are commented.
	#define POST_PROCESS 8
#endif

// 1 - B&W toon shader
// 2 - Toon shader
// 3 - Per-normal light highlighter
// 4 - Music-based scene wave colour highlighting
// 5 - Music-based light intensity modifier - strobe
// 6 - Music equalizer
// 7 - Vignetting
// 8 - Geometry vibration per music
// 9 - Geometry displacement per music
// 10 - Geometry resize per music
//////////////////////////////////////////////////////////////////
// POST PROCESSING // POST PROCESSING // POST PROCESSING // POS //
//////////////////////////////////////////////////////////////////

vec3 toonShadeBW( vec3 c )
{
    float intensity = (c.x + c.y + c.z);
    if (intensity > 1.4)
    {
        return vec3(0.95, 0.95, 0.95);
    }
    else if (intensity > 0.8)
    {
        return vec3(0.65, 0.65, 0.65);
    }
    else if (intensity > 0.4)
    {
        return vec3(0.35, 0.35, 0.35);
    }
    else return vec3(0.2,0.2,0.2);
}

vec3 toonShade( vec3 c )
{
    float intensity = (c.x + c.y + c.z);
    if (intensity > 1.15)
    {
        return vec3(c.x*0.95, c.y*0.95, c.z*0.95);
    }
    else if (intensity > 0.8)
    {
        return vec3(c.x*0.75, c.y*0.75, c.z*0.75);
    }
    else if (intensity > 0.5)
    {
        return vec3(c.x*0.45, c.y*0.45, c.z*0.45);
    }
    else return vec3(c.x*0.2,c.y*0.2,c.z*0.2);
}

float colourPop( vec3 p, vec3 n, float i)
{
    if (dot(p,n) < 0.01)
        return 0.2;
	return i;   
}

vec3 musicLighting( vec3 c, float wave, vec2 uv, float time )
{
    return (c - smoothstep( 0.0, 2., abs(1.5*wave - sin(4.6*3.1415*uv.x + time))));
}

float musicColourPop( vec3 p, vec3 n, float i, float wave, vec2 uv, vec3 ldir )
{
	if (dot(p,n) < mix(0.7,1.0,max(0.0,dot(n,ldir))))
        return i - (2.0 * abs(1.5*wave-uv.x * 1.4));
	return i * (2.5 * abs(2.0*wave-uv.y * 1.4));
}

float colourEQ( vec3 p, vec3 n, float i, float wave, vec2 uv, vec3 ldir )
{
	if (dot(p,n) < mix(0.7,1.0,max(0.0,dot(n,ldir))))
        return 0.36;
	return i * (2.0 * abs(smoothstep( 0.01, 0.5, wave - (uv.x-0.14))));
}

//////////////////////////////////////////////////////////////////
// PRIMITIVE DEFINITION // PRIMITIVE DEFINITION // PRIMITIVE DE //
//////////////////////////////////////////////////////////////////

float sdPlane( vec3 p )
{
	return p.y;
}

float sdSphere( vec3 p, float s )
{
	return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
	vec3 d = abs(p) - b; 
    return min(max(d.x,max(d.y,d.z)),0.0) +length(max(d,0.0));
}

//////////////////////////////////////////////////////////////////
// UTILS METHODS // UTILS METHODS // UTILS METHODS // UTILS MET // 
//////////////////////////////////////////////////////////////////

vec4 getCloser( vec4 d1, vec4 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec4 getIntersect( vec4 d1, vec4 d2 )
{
    return (d1.x > d2.x) ? d1 : d2;
}

//////////////////////////////////////////////////////////////////
// MAP RETURNS THE CLOSER INTERSECTION AND ITS COLOUR // MAP RE //
//////////////////////////////////////////////////////////////////

vec4 map( in vec3 pos, float wave, int post )
{
    float musicHeight = wave*0.3;
	// plane definition
	float planeDistance = sdPlane(pos);
	vec3 planeColour = vec3(500.0);

	// sphere definition
	float sphereRadius = 0.5;
    float sphereDistance;
    if (post == 8)sphereDistance = sdSphere( pos-vec3( 0.0, sphereRadius + musicHeight, 0.0), sphereRadius );
    else if (post == 10)sphereDistance = sdSphere( pos-vec3( 0.0, sphereRadius + musicHeight, 0.0), sphereRadius + musicHeight*0.2 );
    else sphereDistance = sdSphere( pos-vec3( 0.0, sphereRadius, 0.0), sphereRadius );
    if (post == 9)sphereRadius +=  sqrt(sqrt(sqrt(musicHeight*7.)));
	vec3 sphereColour = vec3(0.3, 0.0, 0.2);

    // box definition
    float boxSize = 0.5;
    vec3 boxPosition;
    if (post == 8)
    	boxPosition = vec3(0.5+sphereRadius+boxSize, boxSize + musicHeight, 0.0);
    else boxPosition = vec3(0.5+sphereRadius+boxSize, boxSize, 0.0);
    float boxDistance;
    if (post == 10)boxDistance = sdBox(pos-boxPosition, vec3(boxSize + musicHeight*0.4));
    else boxDistance = sdBox(pos-boxPosition, vec3(boxSize));
    vec3 boxColour = vec3(0.2, 0.1, 0.0);

    // sphere 2 definition
	float sphere2Radius = boxSize*1.3;
    vec3 sphere2Position;
    if (post == 8) sphere2Position= vec3(0.5+sphereRadius+boxSize, boxSize + musicHeight, 0.0);
   	else sphere2Position= vec3(0.5+sphereRadius+boxSize, boxSize, 0.0);
	float sphere2Distance;
    if (post == 10) sphere2Distance = sdSphere( pos-sphere2Position, sphere2Radius + musicHeight*0.3 );
    else sphere2Distance = sdSphere( pos-sphere2Position, sphere2Radius );

    // mapping
	vec4 res = getIntersect( vec4( boxDistance, boxColour ), vec4( sphere2Distance, boxColour ) );
    res = getCloser( res, vec4( sphereDistance, sphereColour ) );
   	res = getCloser( res, vec4( planeDistance, planeColour) );

    for (int i = 0; i < 4; i++) 
    {
        for (int j = 0; j < 4; j++)
        {         
            // the first two positions already contain a primitive
            if ( i == 0 && (j==0 || j==1) ) continue;

            // place auto-generated boxes in the scene
            if (post == 8) boxPosition = vec3((0.5+sphereRadius+boxSize)*float(j), boxSize + musicHeight, (0.5+sphereRadius+boxSize)*float(i));
            else boxPosition = vec3((0.5+sphereRadius+boxSize)*float(j), boxSize, (0.5+sphereRadius+boxSize)*float(i));
            if (post == 10) boxDistance = sdBox(pos-boxPosition, vec3(boxSize + musicHeight*0.4));
            else boxDistance = sdBox(pos-boxPosition, vec3(boxSize));
            res = getCloser( res, vec4( boxDistance, vec3(0.7/float(j+1), 0.6/float(i+1), 1.0/float(i+j+3)) ) );
        }
    }

	return res;
}

//////////////////////////////////////////////////////////////////
// 3D NORMAL OF POINT // 3D NORMAL OF POINT // 3D NORMAL OF POI //
//////////////////////////////////////////////////////////////////

vec3 calcNormal( in vec3 pos, float wave, int post )
{
	vec2 eps = vec2( SMALL_AMOUNT, 0.0 );

	// amazingly clever function to find the normal of a point in space
	// knowing the distance to the camera of the points around it	
	vec3 nor = vec3(
		map(pos+eps.xyy, wave, post).x - map(pos-eps.xyy, wave, post).x,
		map(pos+eps.yxy, wave, post).x - map(pos-eps.yxy, wave, post).x,
		map(pos+eps.yyx, wave, post).x - map(pos-eps.yyx, wave, post).x );

    //float t = 0.0;
  	//t += normalize(texture(iChannel0,.3+pos.xz/6e3,-99.)).x;

	return normalize(nor);
}

//////////////////////////////////////////////////////////////////
// CAMERA METHODS // CAMERA METHODS // CAMERA METHODS // CAMERA //
//////////////////////////////////////////////////////////////////

mat3 setCamera( in vec3 eye, in vec3 center, float upAngle )
{
	vec3 front = normalize(center-eye);
	vec3 worldUp = vec3(sin(upAngle), cos(upAngle), 0.0);
	vec3 right = normalize( cross(front, worldUp) );
	vec3 cameraUp = normalize( cross(right, front) );
	return mat3( right, cameraUp, front );
}

vec4 castRay( in vec3 eye, in vec3 rayDir, float wave, int post )
{
	float tmin = 1.0;
	float tmax = 50.0;

	float tHit = tmin;
	vec3 colour = vec3(-1.0);
	for( int i=0; i<1550; i++ )
	{
		vec4 res = map( eye+rayDir*tHit, wave, post );
		// if intersection is too close or too far break
		if( res.x < SMALL_AMOUNT || tHit > tmax ) break;
		tHit += res.x; // tHit
		colour = vec3(res.y, res.z, res.w); // point colour
	}

	if (tHit > tmax) colour = vec3(-1.0);
	return vec4( tHit, colour );
}

//////////////////////////////////////////////////////////////////
// LIGHT METHODS // LIGHT METHODS // LIGHT METHODS // LIGHT MET // 
//////////////////////////////////////////////////////////////////

float getAmbientOcclusion( in vec3 pos, in vec3 nor, float wave, int post )
{
	float occlusion = 0.0;
	float shadowScatter = 1.5;

	const int maxit = 5; // maxit shades
	for( int i=1; i<=maxit; i++ )
	{
		float height = 0.01 + 0.1*float(i)/float(maxit);
		vec3 shadowPos =  nor * height + pos; // 3D point that might cast a shadow
		float dist = map( shadowPos, wave, post ).x;
		// (height - dist) avoids a point casting a shadow over itself
		occlusion += (height-dist)*shadowScatter;
		// objects far cast less shadow
		shadowScatter *= 0.75;
	}
	return clamp( 1.0 - 3.0*occlusion, 0.0, 1.0 );	
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax, float wave, int post )
{
	float res = 1.0;
	float t = mint;
	for( int i=0; i < int(SAMPLES); i++ )
	{
		float h = map( ro + rd*t, wave, post).x;
		res = min( res, SAMPLES/2.0 * h/t );
		t += clamp( h, 0.02, 0.10 );
		if( h < 0.001 || t>tmax ) break;
	}
	return clamp( res, 0.0, 1.0 );
}

//////////////////////////////////////////////////////////////////
// MAIN // MAIN // MAIN // MAIN // MAIN // MAIN // MAIN // MAIN //
//////////////////////////////////////////////////////////////////

vec3 render( in vec3 eye, in vec3 rayDir, float wave, vec2 uv, int post )
{
    float auxwave = wave;
    if(post != 8 && post != 9 && post != 10)
        wave = 0.0;
	// background color
	vec3 finalColour = vec3(0.0);
	vec4 res = castRay(eye, rayDir, wave, post);

	float tHit = res.x;
	vec3 hitColour = vec3(res.y, res.z, res.w);

	// if no intersection was found, the pixel colour is not changed
	if( hitColour.x + hitColour.y + hitColour.z > -0.5 )
	{
		vec3 pos = eye + tHit*rayDir;
		vec3 nor = calcNormal( pos, wave, post );
		vec3 ref = reflect( rayDir, nor );

		// material		
		if( hitColour.x > 100.0 )
		{   
			float tileSize = hitColour.x/700.0;
			float f = mod( floor(tileSize*pos.z) + floor(tileSize*pos.x), 2.0);
			finalColour = 0.05+f*vec3(0.25);
		}
        else
        {
            finalColour = hitColour;
        }

		// lighting		
		float occlusion = getAmbientOcclusion( pos, nor, wave, post );
        float howfastistime = 0.3;
		vec3  lightdirection = normalize( vec3(-1.5, 1.0, 0.5) ); //ligth direction

		// phong
		float ambient = clamp( nor.y, 0.0, 0.0 );
		float diffuse = clamp( dot( nor, lightdirection ), 0.0, 2.0 );
		float specular = pow(clamp( dot( ref, lightdirection ), 0.0, 1.0 ), 20.0);

		float reflection = softshadow( pos, ref, 0.01, 1.1, wave, post );
		diffuse *= softshadow( pos, lightdirection, 0.05, 1.0, wave, post );


        float light = 0.2 + ambient * 0.1; // ambient light
        light += diffuse * 2.0 ; // diffuse light
		light += specular * 5.0 * diffuse; // specular light

		light += reflection * 0.3;		

        if(post == 3)
        	light = colourPop( pos, nor, light );

        if(post == 5)
        	light = musicColourPop( pos, nor, light, auxwave , uv, lightdirection);

        if(post == 6)
        	light = colourEQ( pos, nor, light, auxwave, uv, lightdirection);

		finalColour *= light * occlusion; // ambient occlusion
		finalColour *= exp( -tHit*SMALL_AMOUNT*1000.0 ); // distance matters

	}

	return vec3( clamp(finalColour,0.0,1.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int post = 0;
#ifdef DEMO_MODE
    post = int(mod((iTime/5.),11.));
#endif
#ifndef DEMO_MODE && RAY_MODE
    post = POST_PROCESS;
#endif
	vec2 q = fragCoord.xy/iResolution.xy;
	vec2 screenPoint = -1.0 + 2.0*q;
	screenPoint.x *= iResolution.x/iResolution.y;
	vec2 mo = iMouse.xy/iResolution.xy;

	float time = 15.0 + iTime;

	// camera	
	//vec3 eye = vec3( 3.2*cos(0.1*time + 0.5*mo.x), 1.5, 3.2*sin(0.1*time + 0.5*mo.y));
    vec3 eye = vec3( -1.5 + 2.0*mo.x, 2.5 + 2.0*mo.y, -1.5);
	vec3 center = vec3( 1.5, 0.0, 1.5 );

    // music management
    float wave = texture( iChannel0, vec2(q.y*0.5,0.75) ).x;

	// camera-to-world transformation
	mat3 camera = setCamera( eye, center, 0.0 );

	// ray direction
	vec3 rayDir = camera * normalize( vec3(screenPoint.xy,2.0) );

	// render	
	vec3 rayColor = render( eye, rayDir, wave, q, post );

	// nicer color 
	rayColor = pow( rayColor, vec3(0.5) );
    if (post == 1)
   		rayColor = toonShadeBW( rayColor );
    else if(post == 2)
   		rayColor = toonShade( rayColor );
    else if(post == 4)
   		rayColor = musicLighting( rayColor, wave, q, time );
	else if(post == 7)
		rayColor *= 0.3 + 0.7*pow( 4.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.3 );
	fragColor=vec4( rayColor, 1.0 );
}
