vec2 Window1Position = vec2(0.35,0.25);
vec2 Window2Position = vec2(0.65,0.25);
vec2 HousePosition = vec2 (0.5,0.3);
vec2 ChimneyPosition = vec2(0.25,0.429);
vec2 DoorPosition = vec2(0.5,0.05);

float WindowLength = 0.05;
float WindowHeight = 0.05;
float HouseLength = 0.3;
float HouseHeight = 0.2;
float ChimneyLength = 0.05;
float ChimneyHeight = 0.06;
float DoorHeight = 0.07;
float DoorLength = 0.05;

float houseLayerOn = 1.0;
float windowLayerOn = 1.0;
float ChimneyLayerOn = 1.0;

vec3 BGColor = vec3(1.0,1.0,1.0);
vec3 FGColor = vec3(0.0,0.0,1.0);
vec3 WindowColor = vec3(0.839216,0.839216,0.839216);
vec3 ChimneyColor = vec3(0.180392, 0.545098 ,0.341176);
vec3 DoorColor = vec3(0.545098,0.270588,0.0745098);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
	{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y *= iResolution.y / iResolution.x;
    HousePosition.y *= iResolution.y / iResolution.x;
    vec4 Background = vec4(BGColor, 1.0);
    vec4 Layer1;
    vec4 Layer2;
    vec4 Layer3;
    vec4 Layer4;

    float lengthX = (1.0 - step(HouseLength, length(HousePosition.x-uv.x)));
    float lengthY = (1.0 - step(HouseHeight, length(HousePosition.y-uv.y)));

    float WindowLengthX = (1.0 - step(WindowLength, length(Window1Position.x-uv.x)));
    float WindowLengthY = (1.0 - step(WindowHeight, length(Window1Position.y-uv.y)));

    WindowLengthX += (1.0 - step(WindowLength, length(Window2Position.x-uv.x)));
    WindowLengthY += (1.0 - step(WindowHeight, length(Window2Position.y-uv.y)));

    float ChimneyLengthX = (1.0 - step(ChimneyLength, length(ChimneyPosition.x-uv.x)));
    float ChimenyLengthY = (1.0 - step(ChimneyHeight, length(ChimneyPosition.y-uv.y)));

    float DoorLengthX = (1.0 - step(DoorLength, length(DoorPosition.x - uv.x)));
    float DoorLengthY = (1.0 - step(DoorHeight, length(DoorPosition.y - uv.y)));    


      Layer1 = vec4(FGColor, (lengthX*lengthY) * houseLayerOn);

      Layer2 = vec4(ChimneyColor, (ChimneyLengthX*ChimenyLengthY ) * ChimneyLayerOn);

      Layer3 = vec4(WindowColor, (WindowLengthX*WindowLengthY) * windowLayerOn);

      Layer4 = vec4(DoorColor, (DoorLengthX * DoorLengthY));




    vec4 Composite = mix(Background, Layer1, Layer1.a);
        Composite = mix(Composite, Layer2, Layer2.a);
        Composite = mix(Composite, Layer3, Layer3.a);
        Composite = mix(Composite, Layer4, Layer4.a);
        fragColor = Composite;


}
