const float B = 2.0;
const float iterations = 400.0;

vec2 madelbrot(vec2 z, vec2 c) {
	return vec2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y ) + c;
}

float d2(vec2 a, vec2 b) {
    return 0.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //fragColor = abs(vec4(pixelToDevice(fragCoord, iResolution.xy), 0.0, 1.0));
    //return;
    vec4 offsetAndZoom = texelFetch( iChannel0, ivec2(0, 0), 0);
    vec2 offset = offsetAndZoom.xy;
    float zoom = offsetAndZoom.z;
    vec2 pixelCoords = pixelToDevice(fragCoord.xy, iResolution.xy);
    vec2 c = pixelCoords / zoom;
    c += offset;
    vec2 z = vec2(0.0);

    float count = 0.0;
    for (float i = iterations; i > 0.0; i -= 1.0) {
        z = madelbrot(z, c);
        if( dot(z,z)>(B*B) ) {
        	count = i;
            break;
        }
    }


    if ( count == 0.0) {
//		fragColor = vec4(vec2(count / iterations),0.5+0.5*sin(iTime),1.0);
        fragColor = vec4(vec2(0.0), offsetAndZoom.x, 1.0);
    } else {
        vec3 color = hue2rgb(count / iterations);
		fragColor = vec4(color,1.0);
    }
}

// Numbers are based on JavaScript key codes: https://keycode.info/
const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;

vec2 handleKeyboard(vec2 offset) {
    float velocity = 1. / 100.; // This will cause offset to change by 0.01 each time an arrow key is pressed

    // texelFetch(iChannel1, ivec2(KEY, 0), 0).x will return a value of one if key is pressed, zero if not pressed
    vec2 left = texelFetch(iChannel1, ivec2(KEY_LEFT, 0), 0).x * vec2(-1, 0);
    vec2 up = texelFetch(iChannel1, ivec2(KEY_UP,0), 0).x * vec2(0, 1);
    vec2 right = texelFetch(iChannel1, ivec2(KEY_RIGHT, 0), 0).x * vec2(1, 0);
    vec2 down = texelFetch(iChannel1, ivec2(KEY_DOWN, 0), 0).x * vec2(0, -1);

    offset += (left + up + right + down) * velocity;

    return offset;
}


