void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    //float minHeight = (sin(iTime * 2.0) + 1.0) * 0.5;
    float minHeight = 1.0 - iMouse.x / iResolution.x;

    float height = 1.0;
    float c = 0.0;
    do {
        c = max(texture(iChannel0, uv / height).r * height, c);
        height *= 0.5;
    } while(c < height && height > minHeight);

	fragColor = vec4(vec3(c), 1.0);
}
