#define count  10.0
#define pi  3.141592654
#define tao  2.0*pi
#define horizontalColorFrom  vec4(1,0,0,1.0)
#define horizontalColorTo  vec4(0,1,0,1.0)

#define verticalColorFrom  vec4(1,1,0,1.0)
#define verticalColorTo  vec4(0,1,1,1.0)

vec3 lerp(vec3 colorone, vec3 colortwo, float value)
{
	return (colorone + value*(colortwo-colorone));
}
vec4 lerp(vec4 colorone, vec4 colortwo, float value)
{
	return (colorone + value*(colortwo-colorone));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	//fragColor = vec4( col, 1.0 );
	//float value = sin(uv.x*10.0*);

    //fragColor = vec4(value,value,value,1);
	//fragColor = texture(iChannel0,uv*10.0);

    float verticalValue = sin(uv.x*count*tao+iTime);
    float horiaontalValue = sin(uv.y*count*tao+iTime);

    vec4 vColor = vec4(verticalValue,verticalValue,verticalValue,1.0);
    vec4 hColor = vec4(horiaontalValue,horiaontalValue,horiaontalValue,1.0);

    vec4 patternColor = (vColor+hColor)*(vColor-hColor);

    vec4 hLerpColor = lerp(horizontalColorFrom,horizontalColorTo,uv.x);
    vec4 vLerpColor = lerp(verticalColorFrom,verticalColorTo,uv.y);


    fragColor = hLerpColor*vLerpColor*patternColor;

    //*fract(uv.x)*fract(uv.y)*vec4(0.8,0.4,0,1.0);

    //fragColor = vec4(uv.x,uv.x,uv.x,1.0);
}
