void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = (iTime*0.1);

    vec2 p = fragCoord.xy / iResolution.xy;

    vec3 T = texture( iChannel1, p ).xyz;
    float N = texture( iChannel0, vec2(p.x*2.0, p.y*2.0+time) ).x;
    float Z = texture( iChannel0, vec2(p.x*3.0+time*0.1, p.y*3.0+time) ).x;


	float col = 1.0;//smoothstep( 0.1, 0.5, N );

    float precip = 0.5;

	float sn = smoothstep( 1.0-precip, 1.0, Z )*0.4 + smoothstep( 1.0-precip, 1.0, N )*0.4;

    fragColor = vec4( T.x*col +  sn, T.y*col + sn, T.z*col + sn, 1.0 );
}
