/*
Author: Scott Aquino
Class: EGP 200-03
Assignment: Layered House
Date Assigned: September 20, 2017
Date Due: September 24, 2017

Description: Make a house with a chimney and windows that can be turned on and off by layer

Certification of Authenticity: I certify that this is entirely my own work, except where I have given
fully‐documented references to the work of others. I understand the definition and
consequences of plagiarism and acknowledge that the assessor of this assignment
may, for the purpose of assessing this assignment:
‐ Reproduce this assignment and provide a copy to another member of academic
‐ staff; and/or Communicate a copy of this assignment to a plagiarism checking
‐ service (which may then retain a copy of this assignment on its database for the
‐ purpose of future plagiarism checking)
*/

//All my variables
vec2 location1 = vec2(0.5,0.2);
vec2 location2 = vec2(0.7,0.325);
vec2 location3 = vec2(0.5,0.25);

float heightH = 0.1;
float widthH = 0.3;
float heightC = 0.03;
float widthC = 0.02;
float heightW = 0.03;
float widthW = 0.03;

vec3 BgColor = vec3(1.0,1.0,1.0);
vec3 Color1 = vec3(0.4,0.4,0.4);
vec3 Color2 = vec3(0.0,0.0,0.0);
vec3 Color3 = vec3(0.0,1.0,1.0);

//Change these variables to 0.0 to "turn off" the layers
float onBody = 1.0;
float onChimney = 1.0;
float onWindows = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

 vec2 uv = fragCoord.xy / iResolution.xy;

 //Correct for aspect ratio
 uv.y *= iResolution.y / iResolution.x;

 //Declare Layers
 vec4 Layer1 = vec4(BgColor,1.0);
 vec4 Layer2 = vec4(Color1, 1.0);
 vec4 Layer3 = vec4(Color2, 1.0);

 //Window Layers to be combined later
 vec4 Layer4 = vec4(Color3, 1.0);
 vec4 Layer5 = vec4(Color3, 1.0);
 vec4 Layer6 = vec4(Color3, 1.0);
 vec4 Layer7 = vec4(Color3, 1.0);
 vec4 Layer8 = vec4(Color3, 1.0);

 //House
 	Layer2 *= step(length(uv.x - location1.x), widthH) * step(length(uv.y - location1.y), heightH) * onBody;

 //Chimney
	Layer3 *= step(length(uv.x - location2.x), widthC) * step(length(uv.y - location2.y), heightC) * onChimney;

 //Windows
    Layer4 *= step(length(uv.x - location3.x), widthW) * step(length(uv.y - location3.y), heightW);

    //Variable locations of first window
    Layer5 *= step(length(uv.x - location3.x + 0.1), widthW) * step(length(uv.y - location3.y), heightW);
    Layer6 *= step(length(uv.x - location3.x + 0.2), widthW) * step(length(uv.y - location3.y), heightW);
    Layer7 *= step(length(uv.x - location3.x - 0.1), widthW) * step(length(uv.y - location3.y), heightW);
    Layer8 *= step(length(uv.x - location3.x - 0.2), widthW) * step(length(uv.y - location3.y), heightW);

    //Mix all windows into Layer4
    Layer4 = mix(Layer4, Layer5, Layer5.a);
    Layer4 = mix(Layer4, Layer6, Layer6.a);
    Layer4 = mix(Layer4, Layer7, Layer7.a);
    Layer4 = mix(Layer4, Layer8, Layer8.a) * onWindows;


 //Blend the Layers
 vec4 Composite = mix(Layer1, Layer2, Layer2.a);
 Composite = mix(Composite, Layer3, Layer3.a);
 Composite = mix(Composite, Layer4, Layer4.a);
 fragColor = Composite;

}
