const float NUM_COL = 5.0;
const float NUM_ROW = 5.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 checkPos = vec2(NUM_COL, NUM_ROW);
    float aspectRatio = iResolution.x / iResolution.y;
	vec2 uv = floor(checkPos.xy*fragCoord.xy*vec2(aspectRatio, 1.0)/ iResolution.xy);



	fragColor = vec4(vec3(mod(uv.x + uv.y, 2.0)),1.0);
}
