//X Coord Displacement
bool xDisplace = true;

//Y Coord Displacement
bool yDisplace = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float displacement = iTime * 100.0;

    float _xDisplacement = 0.0;
    float _yDisplacement = 0.0;

    if(xDisplace){
        _xDisplacement = displacement;
    }

    if(yDisplace){
        _yDisplacement = displacement;
    }


    vec2 fc = vec2(fragCoord.x + _xDisplacement, fragCoord.y + _yDisplacement);

    vec4 sum = vec4(0.0);

    sum += texture(iChannel0, fc.xy / iResolution.xy);

    fragColor = sum;
}
