vec2 mainSound( in int samp, float time )
{
    // 8 measure sequence
    float seqTime = mod(time * rate / 4.0, 8.0);

    vec2 sound = vec2(0.0);

    float panAmount = mod(seqTime*8.0, 2.0)*0.125 - 0.0625;

    if(seqTime < 3.0){
    	sound += measure1(time);
    }else if(seqTime < 4.0){
        sound += measure2(time);
    }else if(seqTime < 5.0){
        sound += measure1(time);
    }else if(seqTime < 8.0){
        sound += measure2(time);
    }else{
        sound += measure1(time);
    }

    return pan(sound.x, panAmount);
}


