void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/iResolution.xy;
    float resolutionFraction = iResolution.x/iResolution.y;
    vec2 uv = p*vec2(resolutionFraction,1.0);
    vec2 circlepos=vec2(0.5*resolutionFraction,0.5);
    float radius=0.9;
    float col=1.f;
    int i=0;
    while( radius>0.1f )
    {
        ++i;
        vec2 dir=normalize(uv-circlepos);
        float t=iTime*0.5f;
        float p = dot(vec2(cos(t),sin(t)),dir); 
        p=clamp(p,0.01,1.0);
        p=pow(p,100.0);
        vec2 offset=dir*mix(0.0,0.02,p);
        float circle=length( circlepos-uv+offset )-radius;
    	if(i%2==0)
    	{
         	radius -=0.01;
	    	col *= ceil(circle);		// black
    	}else
    	{
         	radius -=0.1;

    		col += 1.f-ceil(circle);	// white
    	}
    }

	fragColor = vec4(col,col,col,1.0);
}
