const int radius = 5;
float dither = 0.05;

bvec2 even (in vec2 xy)
{
    bvec2 even;
    vec2 ret = xy.xy * iResolution.xy;
    even.x = floor(mod(ret.x, 2.0)) == 0.0;
    even.y = floor(mod(ret.y, 2.0)) == 0.0;
	return even;
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453)-0.5;
}

float hp( vec3 color, in vec2 xy )
{
    float c = (color.r*2.0 + color.b + color.g*3.0)/6.0;

	const int kSize = (radius-1)/2;
	float kernel[radius];
	float blur = 0.0;

	float z = 0.0;

	for (int j = 0; j <= kSize; ++j)
	{
		kernel[kSize+j] = kernel[kSize-j] = 1.0;
	}

	for (int j = 0; j < radius; ++j)
	{
		z += kernel[j];
	}

	for (int i=-kSize; i <= kSize; ++i)
	{
		for (int j=-kSize; j <= kSize; ++j)
		{
			blur += kernel[kSize+j]*kernel[kSize+i]
				*texture(iChannel0, xy.xy+vec2(float(i),float(j)) / iResolution.xy).g;
			blur = blur-c+0.5;
		}
	}

	return 1.0-blur/(z*z);
}

vec3 maze (in vec3 color, in vec2 xy)
{
    float a;
    float v = hp(color,xy) + rand(xy)*dither ;

    v = (v < 0.5) ? 0.0 : 1.0;

    bvec2 e = even(xy);

    if (e.x && e.y) a = 1.0 - v;
    else if (e.x && !e.y) a = 1.0;
    else if (!e.x && e.y) a = 0.0;
    else a = v;

    return vec3(a);
}

void mainImage( out vec4 color, in vec2 c )
{
	vec2 xy = c.xy / iResolution.xy;
    vec3 pixel = texture(iChannel0,xy).rgb;
	color = vec4(vec3(maze(maze(pixel,xy),xy)),1.0);
}
